mapper.select:
select *,otype.otype as oo ,registrationstatus.registrationstatus as rr,areanew.whole_name,(select count(*) from omembers where organization.organizationid = omembers.organizationid ) as count from organization left join otype on organization.otype = otype.otypeid left join registrationstatus on organization.registrationstatus = registrationstatus.registrationstatusid and userstatusid in (1,3) left join areanew on organization.district = areanew.area_code and areanew.`level` = 3
organization.userstatusid in (1,3)
and organization.otype = #{organization.otype}
and organization.organizationid like concat('%',#{organization.organizationid},'%')
and organization.oname like concat('%',#{organization.oname},'%')
order by organization.registrationtime limit #{begin},#{size};

select count(*) from organization left join otype on otype.otypeid = organization.otype left join registrationstatus on registrationstatus.registrationstatusid = organization.registrationstatus left join areanew on areanew.area_code = organization.district left join userststus on userststus.userstatusid = organization.userstatusid left join organizeaudit on organizeaudit.organizationid = organization.organizationid left join auditstatus on auditstatus.auditstatusid = organizeaudit.auditstatusid
and organization.organizationid = #{organizationid}
and organization.userstatusid = #{userstatusid}

select *,auditstatus.status as shstatus,userststus.status as usstatus from organization
left join otype on otype.otypeid = organization.otype
left join registrationstatus on registrationstatus.registrationstatusid = organization.registrationstatus
left join areanew on areanew.area_code = organization.district
left join userststus on userststus.userstatusid = organization.userstatusid
left join organizeaudit on organizeaudit.organizationid = organization.organizationid
left join auditstatus on auditstatus.auditstatusid = organizeaudit.auditstatusid


        and organization.organizationid = #{organization.organizationid}


        and organization.userstatusid = #{organization.userstatusid}


order by organization.registrationtime limit #{begin},#{size};
